import { ExpandMore } from "@mui/icons-material";
import {
  Accordion,
  AccordionActions,
  AccordionDetails,
  AccordionSummary,
  Button,
  Chip,
  Stack,
  Typography,
} from "@mui/material";
import React from "react";
export default function Groups({ group, removeGroup, updateGroup }) {
  return (
    <Accordion>
      <AccordionSummary expandIcon={<ExpandMore />} id={group.id}>
        <Typography variant="h5">{group.name}</Typography>
      </AccordionSummary>
      <AccordionDetails sx={{ maxHeight: "60vh", overflowY: "auto" }}>
        <Stack
          direction="row"
          spacing={1}
          justifyContent="center"
          flexWrap="wrap"
        >
          {group.count.map((val, i) => (
            <Chip
              key={i}
              label={val}
              sx={{ fontSize: 14, py: 3 }}
              color={group.next === i ? "warning" : "default"}
            />
          ))}
        </Stack>
      </AccordionDetails>
      <AccordionActions sx={{ justifyContent: "space-between" }}>
        <Button
          variant="outlined"
          color="error"
          onClick={() => removeGroup(group.id)}
        >
          Delete Group
        </Button>
        {group.count.length !== Number(group.next) ? (
          <Button
            variant="outlined"
            color="primary"
            onClick={() =>
              updateGroup({ ...group, next: Number(group.next) + 1 })
            }
          >
            Next
          </Button>
        ) : (
          <Button
            variant="outlined"
            color="warning"
            onClick={() => updateGroup({ ...group, next: 0 })}
          >
            Reset
          </Button>
        )}
      </AccordionActions>
    </Accordion>
  );
}
