import React, { useEffect, useState } from "react";
import { DBConfig } from "./DBConfig";
import { initDB } from "react-indexed-db";
import { useIndexedDB } from "react-indexed-db";
import Groups from "./components/Groups";
import NewGroupForm from "./components/NewGroupForm";
import { blue } from "@mui/material/colors";
import MenuIcon from "./components/MenuIcon";
import { Container } from "@mui/material";
import { useSnackbar } from "notistack";

initDB(DBConfig);

const theme = createTheme({
  palette: {
    mode: "dark",
    primary: blue,
  },
});

export default function App() {
  const [groups, setGroups] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const db = useIndexedDB("Groups");
  const { enqueueSnackbar } = useSnackbar();

  const getAllGroups = () => {
    db.getAll().then(
      (resp) => setGroups(resp),
      (error) => console.error(error)
    );
  };

  useEffect(() => {
    getAllGroups();
  }, []);

  const removeGroup = (id) => {
    db.deleteRecord(id).then(
      (resp) => {
        console.log(resp);
        enqueueSnackbar("Group removed.", { variant: "success" });
      },
      (error) => {
        console.error(error);
        enqueueSnackbar(`${error.target.error}`, { variant: "error" });
      }
    );
  };

  return (
    <>
      <MenuIcon toggle={() => setShowForm(!showForm)} show={showForm} />
      <NewGroupForm
        db={db}
        show={showForm}
        toggle={() => setShowForm(!showForm)}
      />
      <Container>
        {groups.map((group) => (
          <Groups key={group.id} group={group} removeGroup={removeGroup} />
        ))}
      </Container>
    </>
  );
}
