import React, { useState } from "react";
import {
  Button,
  Card,
  Grid,
  IconButton,
  Modal,
  TextField,
  Typography,
} from "@mui/material";
import { Add, Close, Menu } from "@mui/icons-material";
import { useSnackbar } from "notistack";

export default function NewGroupForm({ db, show, toggle, addGroup }) {
  const [groupName, setGroupName] = useState("");
  const [groupMinNum, setGroupMinNum] = useState("");
  const [groupCount, setGroupCount] = useState("");

  return (
    <Modal
      open={show}
      onClose={toggle}
      sx={{
        width: "fit-content",
        maxWidth: "90vw",
        margin: "2rem auto 0 auto",
      }}
    >
      <Card sx={{ py: 3 }}>
        <IconButton
          onClick={toggle}
          color={show ? "error" : "default"}
          sx={menuStyle}
        >
          {show ? (
            <Close sx={{ fontSize: 35 }} />
          ) : (
            <Menu sx={{ fontSize: 35 }} />
          )}
        </IconButton>
        <Typography variant="h6" align="center">
          New Group
        </Typography>
        <Grid container justifyContent="space-evenly" gap={2}>
          <Grid item>
            <TextField
              type="text"
              label="Group Name"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Min Number"
              value={groupMinNum}
              onChange={(e) => setGroupMinNum(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Count"
              value={groupCount}
              onChange={(e) => setGroupCount(e.target.value)}
            />
          </Grid>
          <Grid item xl={3} lg={3} md={3} sm={12} xs={12} align="center">
            <Button
              onClick={() => {
                addGroup({ groupName, groupCount, groupMinNum });
                setGroupName("");
                setGroupMinNum("");
                setGroupCount("");
              }}
              color="success"
              variant="contained"
              disabled={!groupName || !groupMinNum || !groupCount}
            >
              <Add sx={{ fontSize: 30, position: "relative", right: 8 }} /> Add
              Group
            </Button>
          </Grid>
        </Grid>
      </Card>
    </Modal>
  );
}

const menuStyle = {
  position: "absolute",
  right: "5px",
  top: "5px",
};
