import { Delete, ExpandMore } from "@mui/icons-material";
import {
  Accordion,
  AccordionActions,
  AccordionDetails,
  AccordionSummary,
  Box,
  Button,
  Chip,
  IconButton,
  Stack,
  Typography,
} from "@mui/material";
import React from "react";
export default function Groups({
  group,
  removeGroup,
  updateGroup,
  resetGroup,
}) {
  return (
    <Accordion
      expanded={group.expanded}
      onChange={(e, expanded) => updateGroup({ ...group, expanded })}
    >
      <AccordionSummary expandIcon={<ExpandMore />} id={group.id}>
        <Typography variant="h5">{group.name}</Typography>
      </AccordionSummary>
      <AccordionDetails sx={{ maxHeight: "60vh", overflowY: "auto" }}>
        <Stack
          direction="row"
          spacing={1}
          justifyContent="center"
          flexWrap="wrap"
        >
          {group.count
            .filter((num, i) => i < group.next)
            .map((val, i) => (
              <Chip
                key={i}
                label={val}
                sx={{ fontSize: 14, py: 3 }}
                color={group.next === i ? "warning" : "default"}
              />
            ))}
        </Stack>
      </AccordionDetails>
      <AccordionActions sx={{ justifyContent: "space-between" }}>
        <IconButton
          variant="outlined"
          color="error"
          size="large"
          onClick={() => removeGroup(group.id)}
        >
          <Delete />
        </IconButton>
        <Box spacing={4}>
          {group.count.length === Number(group.next) && (
            <Button
              variant="outlined"
              color="warning"
              onClick={() => resetGroup(group)}
            >
              Reset
            </Button>
          )}
          <Button
            variant="outlined"
            color="primary"
            disabled={group.count.length === Number(group.next)}
            onClick={() =>
              updateGroup({ ...group, next: Number(group.next) + 1 })
            }
          >
            Next
          </Button>
        </Box>
      </AccordionActions>
    </Accordion>
  );
}
