import React from "react";
import { Button, Grid, Paper, TextField, Typography } from "@mui/material";
import { Add } from "@mui/icons-material";

export default function NewGroupForm({ db, show }) {
  const addGroup = () => {
    try {
      db.add({
        name: "Test Group",
        count: [2, 3, 4, 1, 5, 6, 3, 3, 5],
        next: 4,
      }).then(
        (resp) => {
          console.log(resp);
        },
        (error) => {
          throw new Error(error.target.error);
        }
      );
    } catch (error) {
      console.error(error);
    }
  };
  return (
    show && (
      <Paper
        elevation={3}
        sx={{ width: "fit-content", maxWidth: "90vw", margin: "0 auto", p: 2 }}
      >
        <Typography variant="h6" align="center">
          New Group
        </Typography>
        <Grid container justifyContent="space-evenly" columnGap={2}>
          <Grid item>
            <TextField type="text" label="Group Name" />
          </Grid>
          <Grid item>
            <TextField type="number" label="Min Number" />
          </Grid>
          <Grid item>
            <TextField type="number" label="Count" />
          </Grid>
          <Grid item xl={2} lg={2} md={2} sm={12} xs={12} align="center">
            <Button
              size="large"
              onClick={addGroup}
              color="success"
              variant="outlined"
            >
              <Add sx={{ fontSize: 30 }} /> Add Group
            </Button>
          </Grid>
        </Grid>
      </Paper>
    )
  );
}
