import React, { useEffect, useState } from "react";
import { IconButton, Typography } from "@mui/material";
import NewGroupModal from "./components/NewGroupModal";
import { initDB, useIndexedDB } from "react-indexed-db";
import { DBConfig } from "./DBConfig";
import { useSnackbar } from "notistack";
import { Add, Close, Menu } from "@mui/icons-material";

initDB(DBConfig);

export default function App() {
  const [show, setShow] = useState(true);
  const [groups, setGroups] = useState([]);
  const toggle = () => setShow(!show);
  const db = useIndexedDB("Groups");
  const { enqueueSnackbar } = useSnackbar();

  const getAllGroups = () => {
    db.getAll().then(
      (resp) => {
        setGroups(resp);
      },
      (error) => {
        console.error(error);
      }
    );
  };

  useEffect(() => {
    getAllGroups();
  }, []);

  const createGroup = ({ groupName, groupCount, groupMin }) => {
    db.add({
      name: groupName,
      count: groupCount,
      min: groupMin,
      next: 0,
      expanded: true,
    }).then(
      (resp) => {
        enqueueSnackbar("Group created!", { variant: "success" });
        getAllGroups();
      },
      (error) => {
        console.error(error);
        enqueueSnackbar("Error while creating group..", { variant: "error" });
      }
    );
  };

  return (
    <>
      <Typography color="white">Made with ‚ù§ by Mein Mann, Wizard</Typography>
      <IconButton
        color="success"
        size="large"
        sx={{ position: "absolute", top: 5, right: 5 }}
        onClick={toggle}
      >
        <Add sx={{ fontSize: 35 }} />
      </IconButton>
      <NewGroupModal show={show} toggle={toggle} createGroup={createGroup} />
    </>
  );
}
