import React, { useState } from "react";
import { Button, Grid, Paper, TextField, Typography } from "@mui/material";
import { Add } from "@mui/icons-material";

export default function NewGroupForm({ db, show }) {
  const [groupName, setGroupName] = useState("");
  const [groupMinNum, setGroupMinNum] = useState(null);
  const [groupCount, setGroupCount] = useState(null);
  const addGroup = () => {
    // if(!groupName){}
    try {
      db.add({
        name: "Test Group",
        count: [2, 3, 4, 1, 5, 6, 3, 3, 5],
        next: 4,
      }).then(
        (resp) => {
          console.log(resp);
        },
        (error) => {
          throw new Error(error.target.error);
        }
      );
    } catch (error) {
      console.error(error);
    }
  };
  return (
    show && (
      <Paper
        elevation={3}
        sx={{ width: "fit-content", maxWidth: "90vw", margin: "0 auto", p: 2 }}
      >
        <Typography variant="h6" align="center">
          New Group
        </Typography>
        <Grid container justifyContent="space-evenly" gap={2}>
          <Grid item>
            <TextField
              type="text"
              label="Group Name"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Min Number"
              value={groupMinNum}
              onChange={(e) => setGroupMinNum(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Count"
              value={groupCount}
              onChange={(e) => setGroupCount(e.target.value)}
            />
          </Grid>
          <Grid item xl={3} lg={3} md={3} sm={12} xs={12} align="center">
            <Button onClick={addGroup} color="success" variant="outlined">
              <Add sx={{ fontSize: 30, position: "relative", right: 8 }} /> Add
              Group
            </Button>
          </Grid>
        </Grid>
      </Paper>
    )
  );
}
