import React, { useEffect, useState } from "react";
import { DBConfig } from "./DBConfig";
import { initDB } from "react-indexed-db";
import { useIndexedDB } from "react-indexed-db";
import Groups from "./components/Groups";
import NewGroupForm from "./components/NewGroupForm";
import MenuIcon from "./components/MenuIcon";
import { Container, Typography } from "@mui/material";
import { useSnackbar } from "notistack";

initDB(DBConfig);

export default function App() {
  const [groups, setGroups] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const db = useIndexedDB("Groups");
  const { enqueueSnackbar } = useSnackbar();

  const getAllGroups = () => {
    db.getAll().then(
      (resp) => {
        setGroups(resp);
        if (resp.length === 0) {
          setShowForm(true);
        }
      },
      (error) => console.error(error)
    );
  };

  useEffect(() => {
    getAllGroups();
  }, []);

  const addGroup = ({ groupName, groupCount, groupMinNum }) => {
    try {
      db.add({
        name: groupName,
        count: shuffle(groupCount, groupMinNum),
        next: 0,
        min: groupMinNum,
        total: groupCount,
        expanded: true,
      }).then(
        (resp) => {
          enqueueSnackbar("Added new group!", { variant: "success" });
          getAllGroups();
          setShowForm(false);
        },
        (error) => {
          enqueueSnackbar(`${error.target.error}`, { variant: "error" });
          throw new Error(error.target.error);
        }
      );
    } catch (error) {
      console.error(error);
    }
  };

  function shuffle(number, minNum) {
    // create array of numbers of 'number' length
    let array = Array.from(
        { length: Number(number) + Number(minNum) },
        (_, i) => i + 1
      ),
      i = array.length,
      j = 0,
      temp;
    while (i--) {
      j = Math.floor(Math.random() * (i + 1));
      // swap randomly chosen element with current element
      temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array.filter((num) => num >= minNum);
  }

  const removeGroup = (id) => {
    try {
      db.deleteRecord(id).then(
        (resp) => {
          enqueueSnackbar("Removed group.", { variant: "success" });
          getAllGroups();
        },
        (error) => {
          enqueueSnackbar(`${error.target.error}`, { variant: "error" });
          throw new Error(error.target.error);
        }
      );
    } catch (error) {}
  };

  const updateGroup = (recordObj) => {
    db.update(recordObj).then(
      (resp) => {
        getAllGroups();
      },
      (error) => {
        enqueueSnackbar(`${error.target.error}`, { variant: "error" });
        throw new Error(error.target.error);
      }
    );
  };

  const resetGroup = ({ id, name, total, min, next, expanded }) => {
    updateGroup({
      id,
      name,
      total,
      min,
      expanded,
      count: shuffle(total, min),
      next: 0,
    });
  };

  return (
    <>
      <Typography variant="body2" color="default" align="white">
        Made with ‚ù§ by Mein Mann, Wizard
      </Typography>
      <MenuIcon toggle={() => setShowForm(!showForm)} show={showForm} />
      <NewGroupForm
        db={db}
        show={showForm}
        toggle={() => setShowForm(!showForm)}
        addGroup={addGroup}
      />
      <Container>
        {groups.map((group) => (
          <Groups
            key={group.id}
            group={group}
            removeGroup={removeGroup}
            updateGroup={updateGroup}
            resetGroup={resetGroup}
          />
        ))}
      </Container>
    </>
  );
}
