import { useIndexedDB } from "react-indexed-db/lib";

function DB() {
  const db = useIndexedDB("Groups");
  return db;
}

function getAllGroups() {
  const groups = DB()
    .getAll()
    .then(
      (resp) => {
        return resp;
      },
      (error) => {
        console.error(error);
        return [];
      }
    );
  return groups;
}

export { DB, getAllGroups };
