import React, { useState } from "react";
import {
  Button,
  Card,
  Grid,
  Modal,
  Paper,
  TextField,
  Typography,
} from "@mui/material";
import { Add } from "@mui/icons-material";

export default function NewGroupForm({ db, show, toggle }) {
  const [groupName, setGroupName] = useState("");
  const [groupMinNum, setGroupMinNum] = useState(undefined);
  const [groupCount, setGroupCount] = useState(undefined);
  const addGroup = () => {
    // if(!groupName){}
    try {
      db.add({
        name: "Test Group",
        count: [2, 3, 4, 1, 5, 6, 3, 3, 5],
        next: 4,
      }).then(
        (resp) => {
          console.log(resp);
        },
        (error) => {
          throw new Error(error.target.error);
        }
      );
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <Modal
      open={show}
      onClose={toggle}
      sx={{
        width: "fit-content",
        maxWidth: "90vw",
        margin: "2rem auto 0 auto",
        p: 2,
      }}
    >
      <Card>
        <Typography variant="h6" align="center">
          New Group
        </Typography>
        <Grid container justifyContent="space-evenly" gap={2}>
          <Grid item>
            <TextField
              type="text"
              label="Group Name"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Min Number"
              value={groupMinNum}
              onChange={(e) => setGroupMinNum(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Count"
              value={groupCount}
              onChange={(e) => setGroupCount(e.target.value)}
            />
          </Grid>
          <Grid item xl={3} lg={3} md={3} sm={12} xs={12} align="center">
            <Button onClick={addGroup} color="success" variant="outlined">
              <Add sx={{ fontSize: 30, position: "relative", right: 8 }} /> Add
              Group
            </Button>
          </Grid>
        </Grid>
      </Card>
    </Modal>
  );
}
