import React, { useEffect, useState } from "react";
import { Typography } from "@mui/material";
import NewGroupModal from "./components/NewGroupModal";
import { useIndexedDB } from "react-indexed-db";
import { DBConfig } from "./DBConfig";

initDB(DBConfig);

export default function App() {
  const [show, setShow] = useState(true);
  const [groups, setGroups] = useState([]);
  const toggle = () => setShow(!show);
  const db = useIndexedDB("Groups");

  const getAllGroups = () => {
    db.getAll().then(
      (resp) => {
        setGroups(resp);
      },
      (error) => {
        console.error(error);
      }
    );
  };

  useEffect(() => {
    getAllGroups();
  }, []);

  const createGroup = ({ groupName, groupCount, groupMin }) => {
    console.log(groupName);
  };

  return (
    <>
      <Typography color="white">Made with ‚ù§ by Mein Mann, Wizard</Typography>

      <NewGroupModal show={show} toggle={toggle} createGroup={createGroup} />
    </>
  );
}
