import React from "react";
import { Delete, ExpandMore } from "@mui/icons-material";
import {
  Accordion,
  AccordionActions,
  AccordionDetails,
  AccordionSummary,
  Button,
  Chip,
  IconButton,
  Stack,
  Typography,
} from "@mui/material";

export default function Group({ group, updateGroup, removeGroup, resetGroup }) {
  return (
    <Accordion
      expanded={group.expanded}
      onChange={(e, expanded) => updateGroup({ ...group, expanded })}
    >
      <AccordionSummary expandIcon={<ExpandMore />}>
        {group.name}
      </AccordionSummary>
      <AccordionDetails>
        <Typography variant="h3" align="center">
          {group.count[group.next - 1] || "Ready!"}
        </Typography>
        <Stack direction="row" flexWrap="wrap">
          {group.count.map((num, i) => {
            return i < group.next && <Chip label={num} />;
          })}
        </Stack>
      </AccordionDetails>
      <AccordionActions sx={{ justifyContent: "space-between" }}>
        <IconButton color="error" onClick={() => removeGroup(group.id)}>
          <Delete />
        </IconButton>
        <div>
          {group.next === Number(group.total) && (
            <Button
              color="warning"
              onClick={() => resetGroup(group)}
              sx={{ mr: 6 }}
            >
              Reset
            </Button>
          )}
          <Button
            color="primary"
            disabled={group.next >= group.total}
            onClick={() =>
              updateGroup({ ...group, next: Number(group.next) + 1 })
            }
          >
            Next
          </Button>
        </div>
      </AccordionActions>
    </Accordion>
  );
}
