import React, { useState } from "react";
import {
  Button,
  Card,
  Grid,
  IconButton,
  Modal,
  TextField,
  Typography,
} from "@mui/material";
import { Add, Close, Menu } from "@mui/icons-material";

export default function NewGroupForm({ db, show, toggle }) {
  const [groupName, setGroupName] = useState("");
  const [groupMinNum, setGroupMinNum] = useState("");
  const [groupCount, setGroupCount] = useState("");
  const addGroup = () => {
    // if(!groupName){}
    try {
      db.add({
        name: groupName,
        count: shuffle(groupCount, groupMinNum),
        next: 0,
      }).then(
        (resp) => {
          console.log(resp);
        },
        (error) => {
          throw new Error(error.target.error);
        }
      );
    } catch (error) {
      console.error(error);
    }
  };

  function shuffle(number, minNum) {
    // create array of numbers of 'number' length
    let array = Array.from({ length: number + minNum }, (_, i) => i + 1),
      i = array.length,
      j = 0,
      temp;
    while (i--) {
      j = Math.floor(Math.random() * (i + 1));
      // swap randomly chosen element with current element
      temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array.filter((num) => num >= minNum);
  }

  return (
    <Modal
      open={show}
      onClose={toggle}
      sx={{
        width: "fit-content",
        maxWidth: "90vw",
        margin: "2rem auto 0 auto",
      }}
    >
      <Card sx={{ py: 3 }}>
        <IconButton
          onClick={toggle}
          color={show ? "error" : "default"}
          sx={menuStyle}
        >
          {show ? (
            <Close sx={{ fontSize: 35 }} />
          ) : (
            <Menu sx={{ fontSize: 35 }} />
          )}
        </IconButton>
        <Typography variant="h6" align="center">
          New Group
        </Typography>
        <Grid container justifyContent="space-evenly" gap={2}>
          <Grid item>
            <TextField
              type="text"
              label="Group Name"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Min Number"
              value={groupMinNum}
              onChange={(e) => setGroupMinNum(e.target.value)}
            />
          </Grid>
          <Grid item>
            <TextField
              type="number"
              label="Count"
              value={groupCount}
              onChange={(e) => setGroupCount(e.target.value)}
            />
          </Grid>
          <Grid item xl={3} lg={3} md={3} sm={12} xs={12} align="center">
            <Button
              onClick={addGroup}
              color="success"
              variant="contained"
              disabled={!groupName || !groupMinNum || !groupCount}
            >
              <Add sx={{ fontSize: 30, position: "relative", right: 8 }} /> Add
              Group
            </Button>
          </Grid>
        </Grid>
      </Card>
    </Modal>
  );
}

const menuStyle = {
  position: "absolute",
  right: "5px",
  top: "5px",
};
